document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".zoom-container-hover").forEach(container=>{let img=container.querySelector("img");img.addEventListener("touchstart",function(e){e.preventDefault()}),img.addEventListener("mouseover",function(){img.classList.add("zoom")}),container.addEventListener("mouseout",function(){img.classList.remove("zoom")}),img.addEventListener("mousemove",function(e){var rect,xPercent;img.classList.contains("zoom")&&(rect=img.getBoundingClientRect(),xPercent=(e.clientX-rect.left)/rect.width*100,e=(e.clientY-rect.top)/rect.height*100,img.style.transformOrigin=xPercent+`% ${e}%`)})})});