document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".carousel-item").forEach(container=>{let img=container.querySelector("img");img.addEventListener("click",function(e){img.classList.contains("zoom")?img.classList.remove("zoom"):img.classList.add("zoom")}),img.addEventListener("mousemove",function(e){var rect=img.getBoundingClientRect(),xPercent=(e.clientX-rect.left)/rect.width*100,e=(e.clientY-rect.top)/rect.height*100;img.style.transformOrigin=xPercent+`% ${e}%`})})});